mapper.select:
SELECT count(1) FROM wis_item WHERE 1 = 1

    AND cname LIKE CONCAT('%',#{cname},'%')


    AND uid like CONCAT('%',#{uid},'%')


    and wtlx=#{wtlx}


    and sid=#{sid}

SELECT * FROM wis_item WHERE 1 = 1

    AND cname LIKE CONCAT('%',#{cname},'%')


    AND uid like CONCAT('%',#{uid},'%')


    and wtlx = #{wtlx}


    and sid=#{sid}

LIMIT #{offset},#{limit}
